data.style.css:
".status-bar{
  position: absolute;
  top: 0;
  left: 0;
  background: rgba(0,0,0,0.7);
  color: gray;
  width: 100%;
  height: 4%;
  z-index: 9999;
  align-items: center;
  justify-content: space-between;
  padding: 5px 15px;
  display: none;
}
.status-bar__status--ready,
.status-bar__status--help{
  display: inline-flex;
  align-items: center;
  cursor: pointer;
  margin-right: 10px;
}
.status-bar__status--ready span{
  width: 15px;
  height: 15px;
  border-radius: 50%;
  background-color: gray;
  display: inline-block;
  margin-right: 10px;
}

.status-bar__status--help span{
  width: 15px;
  height: 15px;
  border-radius: 50%;
  background-color: gray;
  display: i gray;
  margin-right: 10px;
}

.status-bar__status--ready.status-active span{
    background-color: lightgreen;
}
.status-bar__status--ready.status-active {
   color: white;
}
.status-bar__status--help.status-active span{
    background-color: red;
}
.status-bar__status--help.status-active{
  color: white;
}
